import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Form from '../Forms/CreateCustomerForm';


const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

function createData(name, email, phoneNumber, address, city,country) {
  return { name, email, phoneNumber, address, city ,country};
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0,1),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3,1),
  createData('Eclair', 262, 16.0, 24, 6.0,1),
  createData('Cupcake', 305, 3.7, 67, 4.3,1),
  createData('Gingerbread', 356, 16.0, 49, 3.9,1),
];

export default function MusteriTable() {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    axios.get('https://localhost:7012/controller/GetAll')
      .then(response => {
        const newRows = response.data.map(item => createData(item.name,item.email,item.phoneNumber,item.address,item.city,item.country));
        setRows(newRows);
        console.log(response.data,"hyfryhfrtyh");
      })
      .catch(error => {
        console.log(error);
      });
  }, []);
  
  const handleNewCategory = (newCategory) => {
    setRows(prevRows => [...prevRows, createData(newCategory)]);
  };

  return (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 700 }} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Kategori</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <StyledTableRow key={row.name}>
              <StyledTableCell component="th" scope="row">
                {row.name}
              </StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
        <Form onNewCategory={handleNewCategory}/>
      </Table>
    </TableContainer>
  );
}
